import logo1 from '../images/logoo.png';
import logo2 from '../images/ff2.png';


export default function HeaderComponent(props) {

    const scrollToSection = (elementRef) => {
        window.scrollTo({
          top: elementRef.current.offsetTop - 90,
          behavior: "smooth",
        });
    };

    return (
        <div className="header-component">
            <div className="logos">
                {/* <div className="logo-1">
                    <a><img src={logo1} alt='logo'/></a>
                </div> */}
                <div className="logo-2">
                    <a><img src={logo2} alt='logo'/></a>
                </div>
            </div>
            <div className="buttons">
                <button className="button-tr btn-animated hover-grow" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    >
                    Home
                </button>
                <button className="button-tr btn-animated hover-grow" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    onClick={() => scrollToSection(props.aboutComp)}
                    >
                    About
                </button>
                <button className="button-tr btn-animated hover-grow" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    >
                    Features
                </button>
                <button className="button-tr btn-animated hover-grow" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    onClick={() => scrollToSection(props.techComp)}
                    >
                    Technologies
                </button>
                <button className="button-tr btn-animated hover-grow" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    onClick={() => scrollToSection(props.teamComp)}
                    >
                    Team
                </button>
            </div>
        </div>
    );
}
