import Grid from '@mui/material/Grid';
import adobe from '../images/adobe.png';

import DividerComponent from "./DividerComponent";

export default function DownloadComponent() {

    const div = <DividerComponent name="Sources" />;

    const onButtonClick = (path, visfile) => {
        const pdfUrl = path;
        const link = document.createElement("a");
        link.href = pdfUrl;
        link.download = visfile;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    return (
        <div className="download-component">
            {div}
            <Grid container spacing={0}>
                <Grid item xs={4}>
                    <a href={require("../documents/poster.pdf")} download="FashionFusion">
                        <div className='download-box'>
                            <img src={adobe} alt='download poster' style={{'width':'40px', 'height':'40px'}}/>
                            <>Poster</>
                        </div>
                    </a>
                </Grid>
                <Grid item xs={4}>
                    <div>
                        github repo
                    </div>
                </Grid>
                <Grid item xs={4}>
                    <div>
                        source zip
                    </div>
                </Grid>
            </Grid>
        </div>
    );
}
