import { useRef } from "react";

export default function HeaderComponent(props) {

    const scrollToSection = (elementRef) => {
        window.scrollTo({
          top: elementRef.current.offsetTop,
          behavior: "smooth",
        });
    };

    return (
        <div className="header-component">
            <div className="buttons">
                <button className="button-tr btn-animated" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    >
                    Home
                </button>
                <button className="button-tr btn-animated" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    >
                    About
                </button>
                <button className="button-tr btn-animated" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    >
                    Features
                </button>
                <button className="button-tr btn-animated" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    onClick={() => scrollToSection(props.techComp)}
                    >
                    Technologies
                </button>
                <button className="button-tr btn-animated" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    onClick={() => scrollToSection(props.teamComp)}
                    >
                    Team
                </button>
            </div>
        </div>
    );
}
