import './../cirrus-all.css'
import { useRef } from 'react';
import img_hcy from '../images/image1.jpeg';
import img_nic from '../images/n.jpg';

import FooterComponent from './FooterComponent';
import HeaderComponent from './HeaderComponent';
import TechComponent from './TechComponent';
import TeamComponent from './TeamComponent';

export default function MainComponent() {

    const teamComp = useRef(null);
    const techComp = useRef(null);

    const scrollToSection = (elementRef) => {
        window.scrollTo({
          top: elementRef.current.offsetTop,
          behavior: "smooth",
        });
      };

    return (
        <div className='main-component'>
            <div className="header-component">
            <div className="buttons">
                <button className="button-tr btn-animated" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    >
                    Home
                </button>
                <button className="button-tr btn-animated" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    >
                    About
                </button>
                <button className="button-tr btn-animated" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    >
                    Features
                </button>
                <button className="button-tr btn-animated" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    >
                    Technologies
                </button>
                <button className="button-tr btn-animated" 
                    style={{'color': 'var(--gray)', 'fontSize': '15px'}}
                    onClick={() => scrollToSection(teamComp)}
                    >
                    Team
                </button>
            </div>
        </div>
            <div className="others-component">
                <TechComponent ref={techComp}/>
                <div className="team-component" ref={teamComp}>
            {div}
            <div>            
                <div className="team-inst">{nic}</div>
                    <div className="team-studs">
                        {eke}
                        {ahg}
                        {hcy}
                </div>
            </div>
        </div>
            </div>
            <FooterComponent/>
        </div>
    );
}
